package com.aode.buyoapp.LL.bean;import java.io.Serializable;import java.util.List;public class ConsumerQueryProductBean implements Serializable{	private static final long serialVersionUID = 1429388883L;	private List<Info> info;	public List<Info> getInfo() {		return this.info;	}	public void setInfo(List<Info> info) {		this.info = info;	}	public ConsumerQueryProductBean() {}	public ConsumerQueryProductBean(List<Info> info){		super();		this.info = info;	}	public String toString() {		return "ConsumerQueryProductBean [info = " + info + "]";	}}class Info implements Serializable{	private static final long serialVersionUID = 1429388883L;	private String pattern;	private Object business;	private String width;	private long id;	private Object title;	private Object stock;	private String size;	private Object users;	private Object bId;	private Object price;	private String color;	private String picture;	public String getPattern() {		return this.pattern;	}	public void setPattern(String pattern) {		this.pattern = pattern;	}	public Object getBusiness() {		return this.business;	}	public void setBusiness(Object business) {		this.business = business;	}	public String getWidth() {		return this.width;	}	public void setWidth(String width) {		this.width = width;	}	public long getId() {		return this.id;	}	public void setId(long id) {		this.id = id;	}	public Object getTitle() {		return this.title;	}	public void setTitle(Object title) {		this.title = title;	}	public Object getStock() {		return this.stock;	}	public void setStock(Object stock) {		this.stock = stock;	}	public String getSize() {		return this.size;	}	public void setSize(String size) {		this.size = size;	}	public Object getUsers() {		return this.users;	}	public void setUsers(Object users) {		this.users = users;	}	public Object getBId() {		return this.bId;	}	public void setBId(Object bId) {		this.bId = bId;	}	public Object getPrice() {		return this.price;	}	public void setPrice(Object price) {		this.price = price;	}	public String getColor() {		return this.color;	}	public void setColor(String color) {		this.color = color;	}	public String getPicture() {		return this.picture;	}	public void setPicture(String picture) {		this.picture = picture;	}	public Info() {}	public Info(String pattern, Object business, String width, long id, Object title,  Object stock, String size, Object users, Object bId, Object price, String color, String picture){		super();		this.pattern = pattern;		this.business = business;		this.width = width;		this.id = id;		this.title = title;		this.picture = picture;		this.stock = stock;		this.size = size;		this.users = users;		this.bId = bId;		this.price = price;		this.color = color;	}	public String toString() {		return "Info [pattern = " + pattern + ", business = " + business + ", width = " + width + ", id = " + id + ", title = " + title + ", picture = " + picture + ", stock = " + stock + ", size = " + size + ", users = " + users + ", bId = " + bId + ", price = " + price + ", color = " + color + ", picture = " + picture + "]";	}}